from sympy import bernoulli, primerange
def ok(n):
    k = 1
    while 2*k <= n - 3 and bernoulli(2*k).numerator() % n:
        k+=1
    return 2*k <= n - 3
print([n for n in primerange(2, 1101) if ok(n)]) # _Indranil Ghosh_, Jun 27 2017, after _Robert G. Wilson v_

