def A011539(n):
    def f(x):
        l = (s:=str(x)).find('9')
        if l >= 0: s = s[:l]+'8'*(len(s)-l)
        return n+int(s,9)
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
