# OEIS sequence: A030628

from sympy import primepi, primerange, integer_nthroot
def A030628(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        kmin = kmax >> 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return n-1+x-sum(primepi(x//p**4) for p in primerange(integer_nthroot(x,4)[0]+1))+primepi(integer_nthroot(x,5)[0])-primepi(integer_nthroot(x,9)[0])
    return bisection(f,n,n) # _Chai Wah Wu_, Feb 21 2025

