from sympy import isprime, primerange
def ok(p): # does prime p satisfy the property
    s = str(p)
    return all(isprime(int(s[i:])) for i in range(1, len(s)))
print(list(filter(ok, primerange(1, 1000)))) # _Michael S. Branicky_, Sep 01 2021

