# OEIS sequence: A036667

from sympy import integer_log
def A036667(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        kmin = kmax >> 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return n+x-sum((x//3**i).bit_length()+(i&1^1)>>1 for i in range(integer_log(x, 3)[0]+1))
    return bisection(f,n,n) # _Chai Wah Wu_, Jan 30 2025

