from sympy import factorint, isprime
def f(n): return int("".join(str(p)*e for p, e in factorint(n).items()))
def a(n):
    if n == 1: return 1
    fn = n
    while not isprime(fn): fn = f(fn)
    return fn
print([a(n) for n in range(1, 40)]) # _Michael S. Branicky_, Jul 11 2022

