# OEIS sequence: A039752

from sympy import factorint
def aupton(terms):
  alst, k, sopfrk, sopfrkp1 = [], 2, 2, 3
  while len(alst) < terms:
    if sopfrkp1 == sopfrk: alst.append(k)
    k += 1
    fkp1 = factorint(k+1)
    sopfrk, sopfrkp1 = sopfrkp1, sum(p*fkp1[p] for p in fkp1)
  return alst
