from itertools import groupby
def ok(n):
    rl_sums = [0, 0]
    for k, g in groupby(bin(n)[2:]):
        rl = len(list(g))
        rl_sums[rl%2] += rl
    return rl_sums[0] == rl_sums[1]
print(list(filter(ok, range(2078)))) # _Michael S. Branicky_, Sep 11 2021

