# OEIS sequence: A046316

from math import isqrt
from sympy import primepi, primerange, integer_nthroot
def A046316(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return int(n+x-sum(primepi(x//(k*m))-b+1 for a,k in enumerate(primerange(3,integer_nthroot(x,3)[0]+1),2) for b,m in enumerate(primerange(k,isqrt(x//k)+1),a)))
    return bisection(f,n,n) # _Chai Wah Wu_, Oct 18 2024

