# OEIS sequence: A048640

from math import isqrt
from sympy import mobius, primepi, primefactors
def A048640(n):
    if n == 1: return 1
    def f(x): return int(n-sum(mobius(k)*(x//k**2) for k in range(2, isqrt(x)+1)))
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
    return sum(1<<primepi(p) for p in primefactors(m)) # _Chai Wah Wu_, Dec 23 2024

