from sympy import isprime
from itertools import islice
def agen(start=1): # generator of terms
    alst, k, sums = [start], 1, {0} | {start}
    while True:
        yield alst[-1]
        while any(isprime(k + s) for s in sums): k += 1
        alst.append(k)
        sums.update([k + s for s in sums])
