from sympy.core.cache import cacheit
@cacheit
def T(n, k): return 0 if k<0 or k>n else 1 if n==0 else T(n - 1, k - 1) + 3**k*T(n - 1, k)
def a(n): return sum(3**(k*(n - k))*T(n, k) for k in range(n + 1))
