from sympy import isprime
for i in range(2,10**7):
    if isprime(i):
        emirp = int(str(i)[-1::-1])
        if isprime(emirp):
            primemirp = int(str(i)+str(emirp)[1:])
            if isprime(primemirp):
                print(primemirp)
