from sympy import factorint
def ok(n): return sorted(factorint(n).values()) == [1, 2]
print([k for k in range(453) if ok(k)]) # _Michael S. Branicky_, Dec 18 2021
from math import isqrt
from sympy import primepi, primerange, integer_nthroot
def A054753(n):
    def bisection(f,kmin=0,kmax=1):
        while f(kmax) > kmax: kmax <<= 1
        kmin = kmax >> 1
        while kmax-kmin > 1:
            kmid = kmax+kmin>>1
            if f(kmid) <= kmid:
                kmax = kmid
            else:
                kmin = kmid
        return kmax
    def f(x): return n+x-sum(primepi(x//p**2) for p in primerange(isqrt(x)+1))+primepi(integer_nthroot(x,3)[0])
    return bisection(f,n,n) # _Chai Wah Wu_, Feb 21 2025

