# OEIS sequence: A055260

def valuation(n, b):
  v = 0
  while n > 1: n //= b; v += 1
  return v
def aupto(lim):
  pows = [9**i for i in range(valuation(lim-1, 9) + 1)]
  sum_pows = sorted([a+b for i, a in enumerate(pows) for b in pows[i:]])
  return [s for s in sum_pows if s <= lim]
