def ok(n):
    s = str(n)
    return s == "".join(str((int(s[0])+i)%10) for i in range(len(s)))
print([k for k in range(10**6) if ok(k)]) # _Michael S. Branicky_, May 26 2022
from itertools import count, islice
def agen(): # generator of terms
    yield 0
