from itertools import count
from sympy import primorial, isprime
def a(n):
    p = primorial(n)
    return next(m-1 for m in count(p, p) if isprime(m-1) and isprime(m+1))
print([a(n) for n in range(1, 20)]) # _Michael S. Branicky_, Apr 18 2025

