from functools import cache
@cache
def T(n, k):
    if k < 0 or k > n: return 0
    if k == n: return (-1)**n
    return (n + k + 2) * T(n-1, k) - T(n-1, k-1)
for n in range(7): print([T(n,k) for k in range(n + 1)])
