# Generate n-th row (n>1) by checking all n! permutations
from itertools import permutations
def onerow(n):
  row=[0]*(n-1)
  for i in permutations(range(1, n+1)):
    row[max([j[0]+j[1] for j in zip(i, i[1:])])-n-1]+=1
  return row
