# OEIS sequence: A065794

from itertools import combinations
def ok(n):
    s, ss = str(n), 0
    for d in range(len(s)-1, 0, -1):
        for c in combinations(s, d):
            t = int("".join(c))
            ss += t
            if ss > n:
                return False
    return n and ss == n
print([k for k in range(300000) if ok(k)]) # _Michael S. Branicky_, Nov 08 2022

