from sympy import factorint
def A069648(n):
    if (n == 1):
        return 1
    else:
        m = 2
        while True:
            x = sum(int(d) for d in str(m**n))
            if x > 1 and not any(map(lambda x:x%n,factorint(x).values())):
                return m
