# OEIS sequence: A073245

from math import isqrt
from sympy import mobius, divisor_sigma
def A073245(n):
    def f(x): return n+x-sum(mobius(k)*(x//k**2) for k in range(1, isqrt(x)+1))
    m, k = n, f(n)
    while m != k:
        m, k = k, f(k)
    return m*divisor_sigma(m) # _Chai Wah Wu_, Aug 12 2024

