from sympy import primefactors, nextprime
def a(n):
    pf = primefactors(n)
    return sum(p2 != nextprime(p1) for p1, p2 in zip(pf[:-1], pf[1:]))
print([a(n) for n in range(1, 121)]) # _Michael S. Branicky_, Oct 14 2021

