def A076886(n):
  d = 1
  while True:
    half = (d+1)//2
    for left in range(10**(half-1), 10**half):
      strleft = str(left)
      if d%2 == 0:
        m = int(strleft + strleft[::-1])
      else:
        m = int(strleft + (strleft[:-1])[::-1])
