from sympy import isprime
from itertools import islice
def agen(an=7):
    while True:
        yield an
        pow10 = 10**len(str(an))
        while True:
            found = False
            for t in range(pow10+an, 10*pow10+an, pow10):
                if isprime(t):
                    an = t; found = True; break
            if found: break
            pow10 *= 10
print(list(islice(agen(), 18))) # _Michael S. Branicky_, Jun 23 2022

