from math import comb, factorial
def A088141(n):
    def p(m): return comb(n,m)*factorial(m)<<1
    kmin, kmax = 0, 1
    while p(kmax) > n**kmax: kmax<<=1
    while kmax-kmin > 1:
        kmid = kmax+kmin>>1
        if p(kmid) <= n**kmid:
            kmax = kmid
        else:
            kmin = kmid
    return kmin # _Chai Wah Wu_, Jan 21 2025

