from sympy import isprime
def ok(n):
    if not isprime(n): return False
    s = str(n)
    return all(isprime(int(s[:i])+int(s[i:])) for i in range(1, len(s)))
print([k for k in range(357) if ok(k)]) # _Michael S. Branicky_, Oct 14 2024

