from sympy import prime, nextprime
def a(n):
    pstr, pn, p = "2", prime(n), 2
    while int(pstr)%pn != 0:
        p = nextprime(p)
        pstr += str(p)
    return p
print([a(n) for n in range(1, 56)]) # _Michael S. Branicky_, Oct 28 2021

