from sympy import fibonacci
def a(n):
    k=0
    x=0
    while n>0:
        k=0
        while fibonacci(k)<=n: k+=1
        x+=10**(k - 3)
        n-=fibonacci(k - 1)
    return x
def ok(n): return 1 if str(a(n))[-3:]=="000" else 0 # _Indranil Ghosh_, Jun 08 2017
