# OEIS sequence: A109811

nd = 5
t = [n*(n+1)//2 for n in range(int((2*10**nd)**.5))]
a = set()
dig = [''] + list('0123456789')
for x in t:
    s = str(x)
    for i in range(len(s)):
        for c in dig:
            if (i, c) == (0, '') and len(s) > 1 and s[1] == '0': continue
            r = s[:i] + c + s[i+1:]
            if r != s and r and int(r) in t:
                a.add(int(r))
                a.add(x)
a = list(sorted(x for x in a if x < 10**(nd-1)))
print(a) # _Andrey Zabolotskiy_, Oct 05 2024

