from sympy import prevprime, nextprime
def A051700(n):
  return [2, 1, 1][n] if n < 3 else min(n-prevprime(n), nextprime(n)-n)
def a(n):
  if n == 0: return 2
  m = 0
  while A051700(m) != 3*n: m += 1
  return m
print([a(n) for n in range(13)]) # _Michael S. Branicky_, Feb 26 2021

