from sympy import primepi, integer_nthroot, primerange
def A139118(n):
    def f(x): return int(n+sum(primepi(integer_nthroot(x,k-1)[0]) for k in primerange(x.bit_length()+1)))
    m, k = n, f(n)
    while m != k: m, k = k, f(k)
    return m # _Chai Wah Wu_, Feb 22 2025

