def a004488(n): return int("".join([str((3 - i)%3) for i in digits(n, 3)[1:]]), 3)
def a117968(n):
    if n==1: return 2
    if n%3==0: return 3*a117968(n/3)
    elif n%3==1: return 3*a117968((n - 1)/3) + 2
    else: return 3*a117968((n + 1)/3) + 1
