# OEIS sequence: A144972

from sympy import mobius, integer_nthroot
def A144972(n):
    def f(x): return n+x-sum(mobius(k)*(x//k**6) for k in range(1, integer_nthroot(x,6)[0]+1))
    m, k = n, f(n)
    while m != k:
        m, k = k, f(k)
    return m # _Chai Wah Wu_, Aug 05 2024

