from __future__ import division
from itertools import combinations
from sympy import prime, isprime
def A157712(n):
    if n == 1:
        return 11
    if n == 2:
        return 0
    p = prime(n)
    l = p
    while True:
        for i in combinations(range(l),l-p):
            s = ['1']*l
            for x in i:
                s[x] = '0'
            q = int(''.join(s))
            if isprime(q):
                return q
        l += 1 # _Chai Wah Wu_, Nov 05 2015

