# OEIS sequence: A166407

from sympy import floor, jacobi_symbol as J
def a(n):
    l=0
    m=0
    for i in range(1, 2*n + 2):
        if J(i, 2*n + 1)==-1: l+=i
        elif J(i, 2*n + 1)==1: m+=i
    return floor(3*((l - m)/(2*n + 1)))
print([a(n) for n in range(101)]) # _Indranil Ghosh_, Jun 12 2017

