from sympy import isprime
def ok(n):
     if n < 1000 or n > 9999 or not isprime(n): return False
     return isprime(n//100 + n%100) and isprime(sum(map(int, str(n))))
afull = [k for k in range(1001, 10000, 2) if ok(k)]
