def trfact_(n, k):
    return reduce(lambda x, y: x*y, range(k+1, n+1), 1)
def choose_(n,k):
    if k > n/2:
        return trfact_(n,k)/trfact_(n-k,1)
    else:
        return trfact_(n,n-k)/trfact_(k,1)
def a_(n):
    return choose_(n,2) + sum(trfact_(n, n-k)/(2*k) for k in range(4, n+1, 2))

