from itertools import count
from sympy import prime
def A181373(n):
    if n == 1 or n == 3: return 0
    p, c, q, a, b = prime(n), 0, 1, 10, 10
    for m in count(1):
        if m >= b:
            a = 10*a%p
            b *= 10
        c = (c*a + m) % p
        q = q*a % p
        if not (c or (q-1)%p):
            return m # _Chai Wah Wu_, Oct 07 2023

