# alternate program constructing terms directly from primes
from sympy import isprime, primerange
def auptobits(maxbits):
    alst = []
    for p in primerange(3, 1<<maxbits):
        b = bin(p)[2:]; br = b[::-1]; t = int(br, 2)
        alst.extend(t<<i for i in range(maxbits-len(br)+1))
    return sorted(alst)
print(auptobits(7)) # _Michael S. Branicky_, Oct 29 2024

