from sympy.ntheory.digits import digits
def ssd(n, b): return sum(d**2 for d in digits(n, b)[1:])
def a(n):
    m = n**2 - 1
    while m != ssd(m, n): m -= 1
    return m
print([a(n) for n in range(3, 58)]) # _Michael S. Branicky_, Aug 01 2021

