# OEIS sequence: A215199

from sympy import isprime, nextprime
from sympy.ntheory.modular import crt
def A215199(n):
    l = len(str(3**n))-1
    l10, result = 10**l, 2*10**l
    while result >= 2*l10:
        l += 1
        l102, result = l10, 20*l10
        l10 *= 10
        q, qn = 2, 2**n
        while qn <= l10:
            s, sn = 2, 2**n
            while sn <= l10:
                if s != q:
                    a, b = crt([qn,sn],[0,1])
                    if a <= l102:
                        a = b*(l102//b) + a
                    while a < l10:
                        p, t = a//qn, (a-1)//sn
                        if p != q and t != s and isprime(p) and isprime(t):
                            result = min(result,a-1)
                        a += b
                s = nextprime(s)
                sn = s**n
            q = nextprime(q)
            qn = q**n
    return result # _Chai Wah Wu_, Mar 12 2019

