from itertools import count, takewhile
def aupto(lim):
  pows = list(takewhile(lambda x: x < lim, (i**i for i in count(1))))
  sums = (aa+bb for i, bb in enumerate(pows) for aa in pows[i+1:])
  return sorted(set(s for s in sums if s <= lim))
