from sympy import isprime, primerange
def DP(n):
    p = 1
    for i in str(n):
        p *= int(i)
    return p
for pn in primerange(1, 2000):
    dpn = DP(pn)
    if isprime(pn-dpn) and isprime(pn+dpn):
        print(pn, end=', ')
