from itertools import count
from sympy import isprime, prime
def a(n):
    if n == 1: return 2
    pn, c = prime(n), 1
    for d in count(1):
        pow10 = 10**d
        base = pn * pow10
        for i in range(1, pow10, 2):
            t = base + i
            if isprime(t): c += 1
            if c == n: return t
print([a(n) for n in range(1, 41)]) # _Michael S. Branicky_, Jan 19 2023

