from sympy import isprime, nextprime
def a(n):
    while True:
        extends, reach, maxp = -1, {n}, 0
        while len(reach) > 0:
            candidates = (int(str(e)+d) for d in "1379" for e in reach)
            reach1 = set(filter(isprime, candidates))
            extends, reach, maxp = extends+1, reach1, max({maxp}|reach1)
        return maxp
