def bitswap(n):
  # Find first bit = 0.
  m = n
  i = 0
  while (m > 0):
    if m % 2 == 0:
      break
    m = m >> 1
    i = i + 1
  if m == 0:
     return n
  # Find first bit = 1 following that 0.
  while (m > 0):
    if m % 2 == 1:
      break
    m = m >> 1
    i = i + 1
  # Swap
  return n & ~(1 << i) | (1 << (i-1))
