import sympy
n=2
while n>1:
    n1=n-2
    n2=((n**3)-2)
    n3=((n**5)-2)
    ##Check if n1, n2 and n3 are also primes.
    if sympy.ntheory.isprime(n1)== True and sympy.ntheory.isprime(n2)== True and sympy.ntheory.isprime(n3)== True:
        print(n, " , " , n1, " , ", n2, " , ", n3)
    n=sympy.ntheory.nextprime(n)

