def isqrt(a):
    sr = 1 << (a.bit_length() >> 1)
    while a < sr * sr:
        sr >>= 1
    b = sr >> 1
    while b:
        s = sr + b
        if a >= s * s:
            sr = s
        b >>= 1
    return sr
