# OEIS sequence: A253047

from sympy import isprime, primepi, prevprime, nextprime, prime
def A253047(n):
    if n <= 2:
        return n
    if n == 3:
        return 7
    q2, r2 = divmod(n,2)
    if r2:
        q3, r3 = divmod(n,3)
        if r3:
            if isprime(n):
                m = primepi(n)
                if isprime(m):
                    return prime(2*m)
                x, y = divmod(m,2)
                if not y:
                    if isprime(x):
                        return prime(x)
            return n
        if isprime(q3):
            return 2*prevprime(q3)
        return n
    if isprime(q2):
        return 3*nextprime(q2)
    return n # _Chai Wah Wu_, Dec 27 2014

